# --- Sidebar for Input ---
st.sidebar.header("Settings")
symbol = st.sidebar.text_input("Symbol (e.g., BTCUSDT, AAPL)", value="BTCUSDT").upper()
days = st.sidebar.slider("Days of Historical Data", min_value=30, max_value=365, value=90)
